import React, { useEffect, useState } from 'react'
import { useCookies } from "react-cookie";
import axios from "axios"
import { useAppContext } from "../AppContext";

import './style.scss';

function Home() {
  const [posts, setPosts] = useState([]);
  const { user } = useAppContext([]);
  const [cookies] = useCookies(['authToken']);
  const POST_URL = 'http://127.0.0.1:8000/post/'

  useEffect(() => {
    if (user) {
      const fetchPosts = async () => {
        try {
          const response = await axios.get(POST_URL, {
            headers: {
              'Authorization': `Token ${cookies.authToken}`, // 認証トークンをヘッダーに含める
              'Content-Type': 'application/json'
            }
          });
          setPosts(response.data);
          console.log(response.data);
        } catch (error) {
          console.log('fetch posts NG')
        }
      }
      fetchPosts();
    }
  }, [user, cookies.authToken])
  
  return (
    <div className="container">
      {posts.map(post => (
        <div className="post" key={post.id}>
          <p className="post__content">{post.post}</p>
          <p className="post__created-at">{post.created_at}</p>
        </div>
      ))}
    </div>
  );
}

export default Home
