import React, { useEffect, useState } from 'react'
import axios from "axios"

import './style.scss';
import { Cookies } from "react-cookie";

function Home({ loggedInUserId }) {
  console.log(loggedInUserId);

  const [posts, setPosts] = useState([]);
  const POST_URL = 'http://127.0.0.1:8000/post/'
  const token = Cookies.get('authToken');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(POST_URL, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        console.log(response.data.id);
        const userPosts = response.data.filter(post => post.author.id === loggedInUserId);
        setPosts(userPosts);
      } catch (error) {
        console.log('error');
      }
    }

    fetchData();
  }, [loggedInUserId])
  
  return (
    <div className="container">
      {posts.map(post => (
        <div className="post" key={post.id}>
          <p className="post__content">{post.post}</p>
          <p className="post__created-at">{post.created_at}</p>
        </div>
      ))}
    </div>
  );
}

export default Home
